import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { StudentService } from 'src/services/student/student.service';
import { Student } from 'src/models/student';

@Component({
  selector: 'app-student-form',
  templateUrl: './student-form..component.html',
  styleUrls: ['./student-form..component.css']
})
export class StudentFormComponent implements OnInit {
  public studentForm: FormGroup;

  constructor(public formBuilder: FormBuilder, public studentService: StudentService) {
    // Form creation
    this.studentForm = this.formBuilder.group({
      name: [''],
      // Add other student properties here
    });
  }

  ngOnInit() {
  }

  addStudent() {
    const studentToCreate: Student = this.studentForm.getRawValue() as Student;
    this.studentService.addStudent(studentToCreate);
  }
}
<h3>Create a new ticket</h3>
<form class="ticket-form" [formGroup]="ticketForm">
  <label for="title">
    Title <br>
    <input class="input-custom" type="text" id="title" [formControlName]="'title'">
  </label>
  <br>
  <label for="description">
    Description <br>
    <input class="input-custom" type="text" id="description" [formControlName]="'description'">
  </label>
  <br>
  <label for="major">
    Major <br>
    <!-- display all the major option in a select -->
    <select name="major-list" id="major" [formControlName]="'major'">
      <option *ngFor="let selectedMajorType of MAJOR_TYPE" [value]="selectedMajorType">
        {{selectedMajorType}}
      </option>
    </select>
  </label>
  <br>
  <label for="student">
    student <br>
    <!-- display all the student option in a select -->
    <select formControlName="studentID">
      <option *ngFor="let student of students" [value]="student.id">{{ student.nom }} {{ student.prenom }}</option>
    </select>
  </label>
  <br>
  <button type="submit" class="btn-custom" (click)="addTicket()">Create</button>
</form>

