import { Component } from '@angular/core';
import { StudentService } from 'src/services/student/student.service';
import { ActivatedRoute } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';
@Component({
  selector: 'app-student-detail',
  templateUrl: './student-detail.component.html',
  styleUrls: ['./student-detail.component.css']
})
export class StudentDetailComponent {
  public students: any;
  public studentDetailForm: FormGroup;


  constructor(public formBuilder: FormBuilder, public studentService: StudentService,private route: ActivatedRoute) {
    // Form creation
    this.studentDetailForm = this.formBuilder.group({
      notes: [''],
      // Add other student properties here
    });
  }

  ngOnInit() {
    this.studentService.students$.subscribe(students => {
      this.students = students;
    });
    const id = this.route.snapshot.paramMap.get('id') || undefined;
    this.studentService.getStudents(id);
  }




}
<h2>{{ students.nom }} {{ students.prenom }}</h2>


<form [formGroup]="studentDetailForm" (ngSubmit)="onSubmit()">
  <textarea formControlName="notes" [(ngModel)]="student.notes"></textarea>
  <button type="submit">Update Note</button>
  <br>
  <a href="./students">retour</a>
</form>
