import { Component, OnInit } from '@angular/core';
import { StudentService } from 'src/services/student/student.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-students-list',
  templateUrl: './students-list.component.html',
  styleUrls: ['./students-list.component.css']
})
export class StudentsListComponent implements OnInit {
  public students: any;


  constructor(public studentService: StudentService,private router: Router) { }


  ngOnInit() {
    this.studentService.students$.subscribe(students => {
      this.students = students;
    });

  }

  navigateToStudentDetails(studentId: number) {
    this.router.navigate(['/student-details', studentId]);
  }

  deleteStudent(id:number) {
    this.studentService.deleteStudent(id);
  }
}
<a href="./students/add"> ajouter un étudiant</a>

<table class="styled-table">
  <thead>
      <tr>
          <th>ID</th>
          <th>Nom</th>
          <th>Prenom</th>
          <th>Action</th>
      </tr>
  </thead>
  <tbody>
      <tr *ngFor="let student of students" (click)="navigateToStudentDetails(student.id)">
          <td>{{student.id}}</td>
          <td>{{ student.nom }}</td>
          <td>{{ student.prenom }}</td>
          <td><button [value]="student.id"  (click)="deleteStudent(student.id)">supprimer</button></td>
      </tr>
  </tbody>
</table>

