import { Component, OnInit } from '@angular/core';
import { StudentService } from 'src/services/student/student.service';

@Component({
  selector: 'app-students-list',
  templateUrl: './students-list.component.html',
  styleUrls: ['./students-list.component.css']
})
export class StudentsListComponent implements OnInit {
  public students: any;

  constructor(public studentService: StudentService) { }


  ngOnInit() {
    this.studentService.students$.subscribe(students => {
      this.students = students;
    });

  }

  deleteStudent(id:number) {
    this.studentService.deleteStudent(id);
  }
}
<a href="./students/add"> ajouter un étudiant</a>

<div *ngIf="students?.length; else noStudents">
  <p *ngFor="let student of students" >
    <a [routerLink]="['/students', student.id]">
      {{student.id}}-{{ student.nom }}-{{ student.prenom }}
    </a>
    <button [value]="student.id"  (click)="deleteStudent(student.id)">supprimer</button>
  </p>
</div>

<ng-template #noStudents>
  <meta http-equiv="refresh" content="0; URL=new_website_url" />
</ng-template>
